const items = $input.all();

// 1. Get email text (try plain text first, fallback to HTML)
let rawEmailText = $('Gmail').first().json.text || '';

if (!rawEmailText.trim() && $('Gmail').first().json.html) {
 const html = $('Gmail').first().json.html;
 rawEmailText = html
   // Remove script/style blocks entirely
   .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '')
   .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '')
   // Replace common block elements with newlines
   .replace(/<\/(p|div|tr|li|h[1-6])>/gi, '\n')
   .replace(/<br\s*\/?>/gi, '\n')
   // Remove all remaining HTML tags
   .replace(/<[^>]+>/g, ' ')
   // Decode HTML entities
   .replace(/&nbsp;/gi, ' ')
   .replace(/&amp;/gi, '&')
   .replace(/&lt;/gi, '<')
   .replace(/&gt;/gi, '>')
   .replace(/&quot;/gi, '"')
   .replace(/&#\d+;/g, ' ');
}

// 2. Clean text (enhanced from historical version)
let emailBodyTextForLLM = '';
if (rawEmailText.trim()) {
 emailBodyTextForLLM = rawEmailText
   // Unicode normalization (from historical)
   .normalize('NFD')
   // Control & invisible characters
   .replace(/[\x00-\x1F\x7F-\x9F]/g, ' ')
   .replace(/[\u200B-\u200F\u2028-\u202F\u205F-\u206F\uFEFF\u034F\u00AD]/g, '')
   // Special whitespace
   .replace(/[\u00A0\u1680\u2000-\u200A\u3000]/g, ' ')
   // Remove image URLs completely (from historical)
   .replace(/https?:\/\/[^\s]*\.(jpg|jpeg|png|gif|svg|webp)[^\s]*/gi, '')
   // Replace remaining URLs
   .replace(/https?:\/\/[^\s]+/gi, '[link]')
   // Fix punctuation spacing (from historical)
   .replace(/\s+([.,;!?])/g, '$1')
   // Normalize whitespace
   .replace(/\s+/g, ' ')
   .trim();
}

// 3. Build attachment texts MAP (unchanged)
const attachmentTextsMap = {};
const gmailBinaryKeys = Object.keys($('Gmail').first().binary || {});
const hasAttachments = gmailBinaryKeys.length > 0;

for (let i = 0; i < items.length; i++) {
 const $json = items[i].json;
 const text = $json.data?.text || $json.textForLLM || $json.text || '';
 const key = gmailBinaryKeys[i] || `attachment_${i}`;
 if (text.trim()) {
   attachmentTextsMap[key] = text.trim();
 }
}

// 4. Return ONE item
return [{
 json: {
   data: [{
     emailBodyTextForLLM,
     attachmentTextsMap,
     attachmentNames: gmailBinaryKeys,
     hasAttachments,
     subject: $('Gmail').first().json.subject || 'No subject',
     from: $('Gmail').first().json.from || 'Unknown',
     date: $('Gmail').first().json.date || new Date().toISOString()
   }]
 },
 pairedItem: { item: 0 }
}];

