parameters.jsCode:
const cleanedData = $('email-info-hub').item.json.cleaned_email?.data?.[0] || {};
const bodyCore = $('subject-classifier-LM').item.json.output?.body_core || '';
const gmailBinary = $('Gmail').first().binary || {};
const attachmentNames = Object.keys(gmailBinary);

// Early return if no attachments - still include email context for LLM
if (attachmentNames.length === 0) {
  return [{
    json: {
      body_core: bodyCore,
      attachmentTextForLLM: 'No attachments found',
      attachmentName: '',
      subject: cleanedData.subject,
      from: cleanedData.from,
      date: cleanedData.date
    }
  }];
}

const results = [];
for (let i = 0; i < attachmentNames.length; i++) {
  const binaryKey = attachmentNames[i];

  results.push({
    json: {
      body_core: bodyCore,
      attachmentTextForLLM: cleanedData.attachment_texts_map?.[binaryKey] || '',
      attachmentName: binaryKey,
      subject: cleanedData.subject,
      from: cleanedData.from,
      date: cleanedData.date
    },
    binary: { file1: gmailBinary[binaryKey] }
  });
}

return results.length > 0 ? results : [{ json: { message: 'No relevant attachments' } }];

const whitelist = [
    "payments-noreply@google.com",
    "noreply@notifications.digitec.ch",
    "invoice+statements@mail.anthropic.com",
    "uniqued4ve@gmail.com"
];

const senderEmail = $('Gmail').first().json.from.value[0].address.toLowerCase();

if (whitelist.some(e => e.toLowerCase() === senderEmail)) {
    return $input.all();
} else {
    return [];
}
