// Merge defaults with any values passed from calling workflow
const input = $input.first().json || {};
const defaults = {
  sheetId: 'YOUR_DEAL_FINDER_SHEET_ID',
  sheetName: 'Requirements',
  trackingSheetName: 'Tracked Prices',
  priceCheckerWorkflowId: 'YOUR_PRICE_CHECKER_WORKFLOW_ID',
  chatId: 'YOUR_CHAT_ID_1',
  region: 'Switzerland',
  retailers: 'Digitec, Galaxus, Toppreise, IKEA, Interdiscount, MediaMarkt, Brack, Microspot',
  currency: 'CHF'
};

// Input values override defaults
return [{ json: { ...defaults, ...input } }];

// Parse incoming command from menu-handler
const input = $input.first().json;
const text = (input.text || '').trim();
const chatId = input.chatId || $('Config').first().json.chatId;

// Internal operations (called by other workflows)
if (text.toLowerCase() === 'check_prices') {
  return [{ json: { operation: 'check_prices', chatId } }];
}

// Explicit track command
if (text.toLowerCase().startsWith('track ')) {
  const remainder = text.substring(6).trim();
  const urlMatch = remainder.match(/https?:\/\/\S+/);
  const url = urlMatch ? urlMatch[0] : '';
  const notifyMode = remainder.toLowerCase().includes('on_change') ? 'on_change' : 'always';
  return [{ json: { operation: 'track', url, notifyMode, chatId } }];
}

// Untrack / stop tracking
if (text.toLowerCase().match(/^(untrack|stop tracking|stop)\s/)) {
  const identifier = text.replace(/^(untrack|stop tracking|stop)\s+/i, '').trim();
  return [{ json: { operation: 'untrack', identifier, chatId } }];
}

// List tracked items
if (text.toLowerCase().match(/^(tracked|list|tracking|what am i tracking)/)) {
  return [{ json: { operation: 'tracked', chatId } }];
}

// URL detection ‚Äî message contains a URL, treat as track
const urlInText = text.match(/https?:\/\/\S+/);
if (urlInText && !['add','remove','pause','resume','digest'].some(op => text.toLowerCase().startsWith(op))) {
  const url = urlInText[0];
  const notifyMode = text.toLowerCase().includes('on_change') ? 'on_change' : 'always';
  return [{ json: { operation: 'track', url, notifyMode, chatId } }];
}

// Original operations (unchanged)
let operation = 'digest';
let category = '';
let constraints = '';
let maxPrice = '';

if (text.toLowerCase().startsWith('add ')) {
  operation = 'add';
  const parts = text.substring(4).trim().split(/\s+/);
  category = parts[0] || '';
  const priceIdx = parts.findIndex((p, i) => i > 0 && /^\d+/.test(p));
  if (priceIdx > 0) {
    maxPrice = parts[priceIdx];
    constraints = parts.slice(priceIdx + 1).join(' ');
  } else {
    constraints = parts.slice(1).join(' ');
  }
} else if (text.toLowerCase().startsWith('remove ')) {
  operation = 'remove';
  category = text.substring(7).trim();
} else if (text.toLowerCase().startsWith('pause ')) {
  operation = 'pause';
  category = text.substring(6).trim();
} else if (text.toLowerCase().startsWith('resume ')) {
  operation = 'resume';
  category = text.substring(7).trim();
} else if (text) {
  operation = 'digest';
  category = text.toLowerCase();
}

return [{
  json: {
    operation,
    category: category.toLowerCase(),
    constraints,
    maxPrice,
    chatId
  }
}];

// Filter requirements: active only, optional category filter
const command = $('Parse Command').first().json;
const categoryFilter = command.category;
const items = $input.all();

const filtered = items.filter(item => {
  const row = item.json;
  // Only active requirements
  if ((row.status || '').toLowerCase() !== 'active') return false;
  // Optional category filter
  if (categoryFilter && (row.category || '').toLowerCase() !== categoryFilter) return false;
  return true;
});

if (filtered.length === 0) {
  return [{
    json: {
      empty: true,
      message: categoryFilter 
        ? `No active requirements found for "${categoryFilter}"`
        : 'No active requirements found. Use /deals add <category> <price> <constraints> to add one.'
    }
  }];
}

// Pass through filtered items
return filtered;

message.content:
=You are a shopping advisor for {{ $('Config').first().json.region }}. Find the current best value option for:

Category: {{ $json.category }}
Requirements: {{ $json.constraints }}
Budget: {{ $json.max_price }}
Priority: {{ $json.priority }}

Search retailers in {{ $('Config').first().json.region }} ({{ $('Config').first().json.retailers }}, etc.) and recommend 2-3 options with:
- Product name and model
- Current price in {{ $('Config').first().json.currency }}
- Where to buy (retailer name)
- Why it's a good value

Focus on products actually available in {{ $('Config').first().json.region }}. Be concise but include specific prices and retailers.

