// Format Perplexity response with category header
const requirement = $('Loop Requirements').first().json;
const perplexityResponse = $input.first().json;

const content = perplexityResponse.choices?.[0]?.message?.content || 'No recommendations found.';

const emoji = {
  'phone': 'üì±',
  'desk': 'ü™ë',
  'laptop': 'üíª',
  'kitchen': 'üç≥',
  'audio': 'üéß',
  'camera': 'üì∑',
  'tv': 'üì∫',
  'appliance': 'üè†'
};

const icon = emoji[requirement.category.toLowerCase()] || 'üõí';

return [{
  json: {
    category: requirement.category,
    formatted: `${icon} *${requirement.category.toUpperCase()}* (Budget: ${requirement.max_price})\n\n${content}`,
    raw: content
  }
}];

// Combine all formatted results into final digest
const results = $input.first().json.data || [];
const chatId = $('Parse Command').first().json.chatId;

if (results.length === 0) {
  return [{
    json: {
      chatId,
      message: 'üõí No recommendations generated.'
    }
  }];
}

const digest = results.map(r => r.formatted).join('\n\n---\n\n');
const header = `üõí *Deal Finder Digest*\n_${results.length} categor${results.length === 1 ? 'y' : 'ies'} researched_\n\n`;

return [{
  json: {
    chatId,
    message: header + digest
  }
}];

