// Find row to remove by category
const categoryToRemove = $('Parse Command').first().json.category;
const items = $input.all();

let rowIndex = -1;
for (let i = 0; i < items.length; i++) {
  if ((items[i].json.category || '').toLowerCase() === categoryToRemove) {
    rowIndex = i + 2; // +2 for header row and 1-based index
    break;
  }
}

return [{
  json: {
    category: categoryToRemove,
    rowIndex,
    found: rowIndex > 0
  }
}];

// Find row to pause by category
const categoryToPause = $('Parse Command').first().json.category;
const items = $input.all();

let rowIndex = -1;
let currentStatus = '';
for (let i = 0; i < items.length; i++) {
  if ((items[i].json.category || '').toLowerCase() === categoryToPause) {
    rowIndex = i + 2; // +2 for header row and 1-based index
    currentStatus = items[i].json.status || '';
    break;
  }
}

return [{
  json: {
    category: categoryToPause,
    rowIndex,
    found: rowIndex > 0,
    currentStatus
  }
}];

// Find row to resume by category
const categoryToResume = $('Parse Command').first().json.category;
const items = $input.all();

let rowIndex = -1;
for (let i = 0; i < items.length; i++) {
  if ((items[i].json.category || '').toLowerCase() === categoryToResume) {
    rowIndex = i + 2;
    break;
  }
}

return [{
  json: {
    category: categoryToResume,
    rowIndex,
    found: rowIndex > 0
  }
}];

const html = $input.first().json.body || $input.first().json.data || '';
const url = $('Parse Command').first().json.url;
const currency = $('Config').first().json.currency;

let productName = 'Unknown Product';
let currentPrice = null;

// Strategy 1: JSON-LD structured data
const jsonLdBlocks = html.match(/<script[^>]*type=["']application\/ld\+json["'][^>]*>[\s\S]*?<\/script>/gi) || [];
for (const block of jsonLdBlocks) {
  try {
    const jsonStr = block.replace(/<script[^>]*>/, '').replace(/<\/script>/, '');
    const ld = JSON.parse(jsonStr);
    const items = Array.isArray(ld) ? ld : [ld];
    for (const item of items) {
      if (item['@type'] === 'Product' || (item['@type']?.includes?.('Product'))) {
        productName = item.name || productName;
        const offers = item.offers;
        if (offers) {
          const offer = Array.isArray(offers) ? offers[0] : offers;
          if (offer.price) currentPrice = parseFloat(offer.price);
          else if (offer.lowPrice) currentPrice = parseFloat(offer.lowPrice);
        }
      }
    }
  } catch (e) { /* skip malformed JSON-LD */ }
}

// Strategy 2: Open Graph meta tags
if (productName === 'Unknown Product') {
  const ogTitle = html.match(/<meta[^>]*property=["']og:title["'][^>]*content=["']([^"']+)["']/i);
  if (ogTitle) productName = ogTitle[1].replace(/\s*[|\-\u2013].*$/, '').trim();
}
if (currentPrice === null) {
  const ogPrice = html.match(/<meta[^>]*property=["']product:price:amount["'][^>]*content=["']([^"']+)["']/i);
  if (ogPrice) currentPrice = parseFloat(ogPrice[1]);
}

// Strategy 3: HTML title fallback
if (productName === 'Unknown Product') {
  const titleTag = html.match(/<title[^>]*>([^<]+)<\/title>/i);
  if (titleTag) productName = titleTag[1].replace(/\s*[|\-\u2013].*$/, '').trim();
}

// Strategy 4: Regex fallback for Swiss price patterns
if (currentPrice === null) {
  const priceMatch = html.match(/(\d+[.,]\d{2})\s*(?:CHF|\.\u2013)|CHF\s*(\d+[.,]\d{2})|(?:CHF|Fr\.)\s*(\d+)\.\u2013/);
  if (priceMatch) {
    const priceStr = priceMatch[1] || priceMatch[2] || priceMatch[3];
    currentPrice = parseFloat(priceStr.replace(',', '.'));
  }
}

// Extract domain from URL
const domain = url.match(/https?:\/\/(?:www\.)?([^/]+)/)?.[1] || '';
const today = new Date().toISOString().split('T')[0];

return [{
  json: {
    url,
    product_name: productName,
    domain,
    current_price: currentPrice,
    currency,
    previous_price: null,
    lowest_price: currentPrice,
    highest_price: currentPrice,
    first_tracked: today,
    last_checked: today,
    status: 'active',
    notify_mode: $('Parse Command').first().json.notifyMode || 'always',
    price_threshold: null,
    chatId: $('Parse Command').first().json.chatId
  }
}];

// Find tracked item by name or URL substring
const identifier = ($('Parse Command').first().json.identifier || '').toLowerCase();
const items = $input.all();

let rowIndex = -1;
let matchedName = '';
for (let i = 0; i < items.length; i++) {
  const row = items[i].json;
  const name = (row.product_name || '').toLowerCase();
  const url = (row.url || '').toLowerCase();
  if (name.includes(identifier) || url.includes(identifier)) {
    rowIndex = i + 2; // +2 for header row and 1-based index
    matchedName = row.product_name || row.url;
    break;
  }
}

return [{
  json: {
    identifier,
    rowIndex,
    found: rowIndex > 0,
    matchedName
  }
}];

const items = $input.all();
const chatId = $('Parse Command').first().json.chatId;
const activeItems = items.filter(i => i.json.url);

if (activeItems.length === 0) {
  return [{ json: { chatId, message: '\ud83d\udccb No tracked items.\n\nUse /deals track <url> to start tracking a product.' } }];
}

const lines = activeItems.map((item, idx) => {
  const r = item.json;
  const statusIcon = r.status === 'paused' ? '\u23f8\ufe0f ' : '';
  const priceInfo = r.current_price != null && r.current_price !== '' ? `${r.current_price} ${r.currency}` : 'checking...';

  let changeInfo = '';
  if (r.previous_price != null && r.previous_price !== '' && r.current_price != null && r.current_price !== '' && Number(r.previous_price) !== Number(r.current_price)) {
    const prev = Number(r.previous_price);
    const curr = Number(r.current_price);
    const pct = (((curr - prev) / prev) * 100).toFixed(1);
    const arrow = curr < prev ? '\ud83d\udcc9' : '\ud83d\udcc8';
    changeInfo = ` (was ${prev}, ${pct > 0 ? '+' : ''}${pct}%) ${arrow}`;
  }

  const notifyLabel = r.notify_mode === 'on_change' ? '\ud83d\udd15 On change' : r.notify_mode === 'threshold' ? `\ud83d\udcb0 < ${r.price_threshold}` : '\ud83d\udd14 Daily';

  return `${idx + 1}. ${statusIcon}*${r.product_name}*\n   \ud83d\udcb0 ${priceInfo}${changeInfo}\n   \ud83d\udd17 ${r.domain} | ${notifyLabel}`;
});

const message = `\ud83d\udccb *Tracked Items* (${activeItems.length})\n\n${lines.join('\n\n')}`;
return [{ json: { chatId, message } }];
