const input = $input.first().json || {};
return [{ json: {
  sheetId: input.sheetId || 'Steward_Deals',
  trackingSheetName: input.trackingSheetName || 'Tracked Prices',
  currency: input.currency || 'CHF',
  chatId: input.chatId || 'YOUR_CHAT_ID_1'
} }];

const items = $input.all();
const active = items.filter(i => i.json.url && (i.json.status || '').toLowerCase() === 'active');

if (active.length === 0) {
  return [{ json: { empty: true } }];
}

return active;

const html = $input.first().json.body || $input.first().json.data || '';
const tracked = $('Loop Items').first().json;
const currency = $('Config').first().json.currency;
const url = tracked.url;

let newPrice = null;

// Strategy 1: JSON-LD structured data
const jsonLdBlocks = html.match(/<script[^>]*type=["']application\/ld\+json["'][^>]*>[\s\S]*?<\/script>/gi) || [];
for (const block of jsonLdBlocks) {
  try {
    const jsonStr = block.replace(/<script[^>]*>/, '').replace(/<\/script>/, '');
    const ld = JSON.parse(jsonStr);
    const items = Array.isArray(ld) ? ld : [ld];
    for (const item of items) {
      if (item['@type'] === 'Product' || (item['@type']?.includes?.('Product'))) {
        const offers = item.offers;
        if (offers) {
          const offer = Array.isArray(offers) ? offers[0] : offers;
          if (offer.price) newPrice = parseFloat(offer.price);
          else if (offer.lowPrice) newPrice = parseFloat(offer.lowPrice);
        }
      }
    }
  } catch (e) { /* skip */ }
}

// Strategy 2: Open Graph meta
if (newPrice === null) {
  const ogPrice = html.match(/<meta[^>]*property=["']product:price:amount["'][^>]*content=["']([^"']+)["']/i);
  if (ogPrice) newPrice = parseFloat(ogPrice[1]);
}

// Strategy 3: Regex for Swiss price patterns
if (newPrice === null) {
  const priceMatch = html.match(/(\d+[.,]\d{2})\s*(?:CHF|\.\u2013)|CHF\s*(\d+[.,]\d{2})|(?:CHF|Fr\.)\s*(\d+)\.\u2013/);
  if (priceMatch) {
    const priceStr = priceMatch[1] || priceMatch[2] || priceMatch[3];
    newPrice = parseFloat(priceStr.replace(',', '.'));
  }
}

const oldPrice = tracked.current_price != null && tracked.current_price !== '' ? Number(tracked.current_price) : null;
const today = new Date().toISOString().split('T')[0];

// Determine change
let priceChanged = false;
let direction = 'unchanged';
if (newPrice !== null && oldPrice !== null && newPrice !== oldPrice) {
  priceChanged = true;
  direction = newPrice < oldPrice ? 'down' : 'up';
}

// Update extremes
let lowestPrice = tracked.lowest_price != null && tracked.lowest_price !== '' ? Number(tracked.lowest_price) : null;
let highestPrice = tracked.highest_price != null && tracked.highest_price !== '' ? Number(tracked.highest_price) : null;
if (newPrice !== null) {
  if (lowestPrice === null || newPrice < lowestPrice) lowestPrice = newPrice;
  if (highestPrice === null || newPrice > highestPrice) highestPrice = newPrice;
}

// Determine notification
const notifyMode = tracked.notify_mode || 'always';
let shouldNotify = false;
if (newPrice !== null) {
  if (notifyMode === 'always') shouldNotify = true;
  else if (notifyMode === 'on_change') shouldNotify = priceChanged;
  else if (notifyMode === 'threshold') {
    const threshold = tracked.price_threshold != null && tracked.price_threshold !== '' ? Number(tracked.price_threshold) : null;
    shouldNotify = threshold !== null && newPrice <= threshold;
  }
}

return [{
  json: {
    url,
    product_name: tracked.product_name,
    domain: tracked.domain,
    old_price: oldPrice,
    new_price: newPrice,
    currency,
    previous_price: oldPrice,
    current_price: newPrice !== null ? newPrice : oldPrice,
    lowest_price: lowestPrice,
    highest_price: highestPrice,
    last_checked: today,
    priceChanged,
    direction,
    shouldNotify,
    notify_mode: notifyMode
  }
}];

const results = $input.first().json.data || [];
const toNotify = results.filter(r => r.shouldNotify);

if (toNotify.length === 0) {
  return [{ json: { priceReport: [], priceSection: '' } }];
}

const lines = toNotify.map(r => {
  if (!r.priceChanged) {
    return `  ${r.product_name}: ${r.new_price} ${r.currency} \u27a1\ufe0f`;
  }
  const pct = (((r.new_price - r.old_price) / r.old_price) * 100).toFixed(1);
  const arrow = r.direction === 'down' ? '\ud83d\udcc9' : '\ud83d\udcc8';
  return `  ${r.product_name}: ${r.old_price} \u2192 ${r.new_price} ${r.currency} ${arrow} (${pct > 0 ? '+' : ''}${pct}%)`;
});

const priceSection = `\n\n\ud83d\udccd *Price Tracker* (${toNotify.length} item${toNotify.length === 1 ? '' : 's'})\n${lines.join('\n')}`;

return [{ json: { priceReport: toNotify, priceSection } }];
