// Ultra-Aggressive Email Text Cleaner for n8n
// Removes ALL formatting artifacts for clean LLM processing

// Process ALL items
const items = $input.all();
const results = [];

for (const item of items) {
  const $json = item.json;

  // Get the raw text from various possible locations
  let rawText = '';

  // Try different paths to find the text
  if ($json.text) {
    rawText = $json.text;
  } else if ($json.data && $json.data.text) {
    rawText = $json.data.text;
  } else if ($json['data.text']) {
    rawText = $json['data.text'];
  } else if ($input.item.json.text) {
    rawText = $input.item.json.text;
  } else if ($input.item.json.data && $input.item.json.data.text) {
    rawText = $input.item.json.data.text;
  }

  // If still no text, return error
  if (!rawText || rawText.trim().length === 0) {
    return {
      json: { // Wrap error in json to maintain standard format
        error: 'No text content found',
        paths_checked: ['$json.text', '$json.data.text', '$json["data.text"]'],
        available_keys: Object.keys($json || {})
      }
    };
  }

  // EXTRACT ATTACHMENT NAMES from item.binary (simple key extraction)
  const attachmentNames = item.binary ? Object.keys(item.binary) : [];

  // STEP 1: NUCLEAR OPTION - Remove ALL non-standard characters first
  let text = rawText
    // Convert to standard ASCII where possible
    .normalize('NFD')
    // Remove ALL control and invisible characters
    .replace(/[\x00-\x1F\x7F-\x9F]/g, ' ')
    // Remove zero-width characters
    .replace(/[\u200B-\u200F\u2028-\u202F\u205F-\u206F]/g, ' ')
    // Remove other invisibles
    .replace(/[\uFEFF\u034F\u00AD]/g, ' ')
    // Remove soft hyphens and special spaces
    .replace(/[\u00A0\u1680\u180E\u2000-\u200A\u3000]/g, ' ')
    // Remove the specific problematic characters from your email
    .replace(/[Â­Íâ€Œ]/g, ' ')
    // Replace ALL multiple spaces with single space
    .replace(/\s+/g, ' ')
    // Trim each line
    .split('\n')
    .map(line => line.trim())
    .join('\n');

  // STEP 2: Extract URLs and create a clean mapping
  const urlRegex = /https?:\/\/[^\s\[\]\(\)<>]+/gi;
  const imageRegex = /\[?https?:\/\/[^\]]*\.(jpg|jpeg|png|gif|svg|webp)[^\]]*\]?/gi;

  // First remove image URLs completely
  text = text.replace(imageRegex, ' ');

  // Then handle other URLs
  const urls = text.match(urlRegex) || [];
  const uniqueUrls = [...new Set(urls)];
  const urlMap = {};

  uniqueUrls.forEach((url, i) => {
    const linkId = `[Link${i + 1}]`;
    urlMap[linkId] = url;
    // Replace URL with placeholder
    text = text.replace(new RegExp(url.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'), 'g'), linkId);
  });

  // STEP 3: Fix spacing issues caused by removed content
  text = text
    // Fix spaces around punctuation
    .replace(/\s+([.,;!?])/g, '$1')
    .replace(/([.,;!?])\s*([a-z])/g, '$1 $2')
    // Remove multiple spaces again
    .replace(/\s+/g, ' ')
    // Fix line breaks - ensure proper spacing
    .replace(/([a-z])\s*\n\s*([a-z])/g, '$1 $2') // Join words split across lines
    .replace(/([.!?])\s*([A-Z])/g, '$1\n$2') // Add line break after sentences
    ;

  // STEP 4: Clean up brackets and special formatting
  text = text
    .replace(/\[\s*\]/g, '') // Remove empty brackets
    .replace(/\s*\[\s*/g, ' [') // Clean bracket spacing
    .replace(/\s*\]\s*/g, '] ')
    .replace(/\s+/g, ' '); // Final space cleanup

  // STEP 5: Smart paragraph detection and formatting
  const lines = text.split('\n');
  const formattedLines = [];
  let currentParagraph = '';

  for (const line of lines) {
    const trimmed = line.trim();
    if (trimmed.length === 0) continue;

    // Check if this looks like a new section/paragraph
    if (trimmed.match(/^(Hallo|Lieber|Sehr geehrte|Details|Bestellung|Noch Fragen|Freundliche|Angebot von|Lieferadresse|Paketversand)/i) ||
        trimmed.match(/^[A-Z].*[.!?]$/) && currentParagraph.length > 50) {
      // Save current paragraph if exists
      if (currentParagraph) {
        formattedLines.push(currentParagraph.trim());
      }
      currentParagraph = trimmed;
    } else {
      // Continue current paragraph
      currentParagraph = currentParagraph ? `${currentParagraph} ${trimmed}` : trimmed;
    }
  }
  // Don't forget the last paragraph
  if (currentParagraph) {
    formattedLines.push(currentParagraph.trim());
  }

  text = formattedLines.join('\n\n');

  // STEP 6: Extract key information
  const extractedInfo = {
    orderNumber: null,
    trackingNumber: null,
    product: null,
    deliveryAddress: null,
    amounts: [],
    dates: []
  };

  // Extract order number
  const orderMatch = text.match(/(?:Bestellung|Order|Auftrag)[\s#:]*(\d{6,})/i);
  if (orderMatch) {
    extractedInfo.orderNumber = orderMatch[1];
  }

  // Extract tracking number (long number that's not the order)
  const numbers = text.match(/\b\d{10,20}\b/g) || [];
  extractedInfo.trackingNumber = numbers.find(n => n !== extractedInfo.orderNumber) || null;

  // Extract product name
  const productMatch = text.match(/tomtoc[^[]*?(?=\[|$)/i);
  if (productMatch) {
    extractedInfo.product = productMatch[0].trim();
  }

  // Extract delivery address
  const addressMatch = text.match(/Lieferadresse\s*([^[]+?)(?=Paket|\[|$)/i);
  if (addressMatch) {
    extractedInfo.deliveryAddress = addressMatch[1].trim();
  }

  // Extract dates
  const dateMatches = text.match(/\d{1,2}[\.\/\-]\d{1,2}[\.\/\-]\d{2,4}/g);
  if (dateMatches) {
    extractedInfo.dates = dateMatches;
  }

  // STEP 7: Create super-clean version for LLM
  const textForLLM = text
    .replace(/\[Link\d+\]/g, '') // Remove all link placeholders
    .replace(/\[\]/g, '') // Remove empty brackets
    .replace(/\s+/g, ' ') // Normalize spaces
    .replace(/\n{3,}/g, '\n\n') // Max 2 line breaks
    .trim();

  // STEP 8: Create a structured summary for LLM
  const attachmentList = attachmentNames.length > 0 
    ? attachmentNames.join(', ')
    : 'No attachments';

  const structuredSummary = `
ORDER DETAILS:
- Order Number: ${extractedInfo.orderNumber || 'Not found'}
- Tracking: ${extractedInfo.trackingNumber || 'Not found'}
- Product: ${extractedInfo.product || 'Not found'}
- Delivery Address: ${extractedInfo.deliveryAddress || 'Not found'}
- Order Date: ${extractedInfo.dates?.[0] || 'Not found'}
- Attachments: ${attachmentList}

ORIGINAL MESSAGE:
${textForLLM}
`.trim();

  // STEP 9: Get metadata safely
  const metadata = {
    subject: $json.subject || $input.item?.json?.subject || 'No subject',
    from: $json.from || $input.item?.json?.from || 'Unknown',
    to: $json.to || $input.item?.json?.to || 'Unknown',
    date: $json.date || $input.item?.json?.date || new Date().toISOString()
  };

  // Add to results array - WITH BINARY DATA INCLUDED
  results.push({
    // 1. JSON Part
    json: {
      attachmentNames: attachmentNames,
      textForLLM: textForLLM,
      structuredSummary: structuredSummary,
      text: text,
      data: {
        text: textForLLM
      },
      ...metadata,
      extracted: extractedInfo,
      linkMapping: Object.keys(urlMap).length > 0 ? urlMap : null,
      stats: {
        originalLength: rawText.length,
        cleanLength: text.length,
        llmLength: textForLLM.length,
        reduction: Math.round((1 - textForLLM.length / rawText.length) * 100) + '%',
        linkCount: Object.keys(urlMap).length
      }
    },
    // 2. Binary Part (The Fix)
    // This passes the input binary data directly to the output
    binary: item.binary 
  });
} // Close the for loop

// Return results directly (we already structured them as {json, binary} above)
return results;

