const items = $input.all();

  // 1. Clean email body
  const rawEmailText = $('Gmail').first().json.text || '';
  let emailBodyTextForLLM = '';

  if (rawEmailText.trim()) {
    const invisibleChars = /[\x00-\x1F\x7F-\x9F]+/g;
    const zeroWidthChars = /[\u200B-\u200F\u2028-\u202F\u205F-\u206F\uFEFF]+/g;
    const weirdWhitespace = /[\u00A0\u1680\u2000-\u200A\u3000]+/g;

    emailBodyTextForLLM = rawEmailText
      .replace(invisibleChars, ' ')
      .replace(zeroWidthChars, '')
      .replace(weirdWhitespace, ' ')
      .replace(/https?:\/\/[^\s]+/gi, '[link]')
      .replace(/\s+/g, ' ')
      .trim();
  }

  // 2. Build attachment texts MAP using Gmail's binary key names
  const attachmentTextsMap = {};
  const gmailBinaryKeys = Object.keys($('Gmail').first().binary || {});
  const hasAttachments = gmailBinaryKeys.length > 0;

  for (let i = 0; i < items.length; i++) {
    const $json = items[i].json;

    // Text is in $json.data.text (based on debug)
    const text = $json.data?.text || $json.textForLLM || $json.text || '';
    const key = gmailBinaryKeys[i] || `attachment_${i}`;

    if (text.trim()) {
      attachmentTextsMap[key] = text.trim();
    }
  }

  // 3. Return ONE item
  return [{
    json: {
      data: [{
        emailBodyTextForLLM: emailBodyTextForLLM,
        attachmentTextsMap: attachmentTextsMap,
        attachmentNames: gmailBinaryKeys,
        hasAttachments: hasAttachments,
        subject: $('Gmail').first().json.subject || 'No subject',
        from: $('Gmail').first().json.from || 'Unknown',
        date: $('Gmail').first().json.date || new Date().toISOString()
      }]
    },
    pairedItem: { item: 0 }
  }];

